import { combineReducers } from 'redux'
import reducerUser from './user';
import reducerLogin from './login';
import reducerItem from './item';
import reducerOrder from './order';
import reducerMenu from './menu';
import reducerDepartment from './department';

const rootReducer = combineReducers({
  user: reducerUser,
  login: reducerLogin,
  item: reducerItem,
  order: reducerOrder,
  menu: reducerMenu,
  department: reducerDepartment
})

export default rootReducer;
