import { memo } from 'react';

function Form(props) {
    const {inputs, onChange, handleSubmit} = props;
  
    return (
        <form className="row g-3" onSubmit = {handleSubmit}>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.font}>Tên sản phẩm</label>
                <input type="text" style={styles.font} className="form-control" name="name" value={inputs.name || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.font}>ĐVT</label>
                <input type="text" style={styles.font} className="form-control" name="unit" value={inputs.unit || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="d-grid gap-3 d-md-flex justify-content-md-end">
                <button style={styles.font} className="btn btn-outline-primary">Thêm sản phẩm</button>
            </div> 
        </form>
    )
}

export default memo(Form);
const styles = {
    font:{
        fontFamily:"Times New Roman",
    }
}
