import axios from 'axios';
const API_URL='http://localhost:8500/bitex/api/';
// http://171.244.205.210:8500/bitex/api/tax
const ourRequest = axios.CancelToken.source();

/*============================= GET API =============================*/
export const getApi = async (endpoint) => {
  try {
    let res = await axios.get(`${API_URL}${endpoint}`,{
    headers: {
      'Content-Type': 'application/json', // Header Content-Type
      Authorization: 'Bearer your_token_here', // Header Authorization (nếu cần thiết)
    }},{CancelToken:ourRequest.token})  
    return res.data
  } catch (error){
    console.log("error API", error)
  }
};
// headers: { Accept: 'text/html, application/json, text/plain, */*' }
/*============================= POST API =============================*/
export const postApi = async (endpoint,data) => {
  try {
    let res = await axios({ method: 'POST',url:`${API_URL}${endpoint}`,data:data}) 
    return res.data
  } catch (error) {
    console.log("error API", error);
  }
};

/*============================= PUT API =============================*/
export const putApi = async (endpoint,data) => {
  try {
    let res = await axios({ method: 'PUT',url:`${API_URL}${endpoint}`,data:data},{CancelToken:ourRequest.token})   
    return res.data
  } catch (error) {
    console.log("error API");
  }
};

/*============================= DELETE API =============================*/
export const deleteApi = async (endpoint) => {
  try {
    let res = await axios({ method: 'DELETE',url:`${API_URL}${endpoint}`,data:null},{CancelToken:ourRequest.token})   
    return res.data
  } catch (error) {
    console.log("error API");
  }
};

/*============================= POST API PICTURE =============================*/
export const postApiImg = async (endpoint,data) => {
  try {
    let res = await axios({ method: 'POST',
                          url:`${API_URL}${endpoint}`,
                          headers :{'Content-Type': 'multipart/form-data'},
                          data:data},
                          {CancelToken:ourRequest.token})   
    return res.data
  } catch (error) {
    console.log("error API");
  }
};
