import * as types from '../type/index';

const initialState =[]
const reducerMenu = (state = initialState, action) => {
  const { type, payload } = action;  
  switch (type) {
    case types.GET_MENU:  
      payload ? state = payload :  state = []
      return [...state] 
    default:
      return state
  }
}

export default reducerMenu;
