import * as types from '../type/index';
import {findIndex} from '../../hooks/index';

const initialState =[]
const reducerOrderLine = (state = initialState, action) => {
  
  const { type, payload } = action;  
  
  switch (type) {
    case types.GETORDERLINE:  /* Lấy đơn hàng Line */
      payload ? state = payload :  state = []
      console.log(state);
      return [...state] 
    case types.SETSTATUSORDERLINE:  /* update trạng thái đơn hàng Line */
       temp=findIndex(state,action.id)
       if(temp!==-1){
        state[temp].status=1;
       }
      return [...state] 
    default:
      return state
      
  }
}

export default reducerOrderLine;
