import { memo } from 'react';

function Form(props) {
    const { item, handeModalApproval } = props;
    
    return (
        <div className="row g-3">
             <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Mã đơn</label>
                <input type="text" style={styles.InputText} name="document" className="form-control" value={item[0]?.DocumentNo} disabled/>
            </div>
            {/* <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Phòng ban</label>
                <input type="text" style={styles.InputText} className="form-control" name="departments" value = {JSON.parse(localStorage.getItem('department')) || ""} disabled/>
            </div> */}
{/* 
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Ngành hàng</label>
                <select className="form-select" onChange={onChange} name="industry" value={inputs ?? ""}>
                    <option value=""> === Chọn ngành hàng === </option>
                    { industry.map(item => (
                        <option key={item.Code} value={item.Code}>
                            {item.Description}
                        </option>))
                    }
                </select>
            </div> */}
            <div className="d-grid gap-3 d-md-flex justify-content-md-end">
                <button style={styles.InputText} onClick={handeModalApproval} className="btn btn-outline-info">Gộp đơn</button>
            </div> 
        </div>
    )
}

export default memo(Form);
const styles = {
    InputText:{
        fontFamily:"Times New Roman",
    }
}
