import { memo, useState } from 'react';
import Select from 'react-select';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';

function Form( props ) {
    const { departments, staff, status, item, onChange, inputs, handleSubmit, handleChange, handleChangeSelect, convertOptions, activityBodyTemplate } = props;
    
    const [globalFilter, setGlobalFilter] = useState(null);

    
    
    const renderHeader = () => {
        return (
            <div className="flex justify-content-between">
                <span className="p-input-icon-left">
                    <i className="pi pi-search" />
                    <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
                </span>
            </div>
        );
    };
    
    const header = renderHeader();

    return (
        <form className="row g-3" onSubmit = { handleSubmit }>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Ngày cần hàng</label>
                <input type="date" style={styles.InputText} className="form-control" name="formDate" value={inputs.formDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Chọn phòng ban</label>
                <Select options = {convertOptions(departments)}
                        onChange = {handleChange("department")}
                        placeholder="Chọn phòng ban"
                        styles={{ container: base => ({ ...base, ...styles.text }) }}/>
            </div>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Chọn nhân viên</label>
                <Select options = {convertOptions(staff)}
                        onChange = {handleChange("staff")}
                        placeholder="Chọn nhân viên"
                        styles={{ container: base => ({ ...base, ...styles.text }) }}/>
            </div>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Nhà cung cấp ưu tiên</label>
                <input type="text" style={styles.InputText} className="form-control" name="supplier" value={inputs.supplier || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style = {styles.InputText}>Trạng thái</label>
                <select className="form-select" style={styles.text} onChange = {handleChangeSelect}>
                    { status && status.length > 0  ? 
                      status.map((item, index) => {
                      return ( <option key={index} value={item.Code}>{item.Code} - {item.Name}</option>) }) : ""  
                  }
                </select>
            </div>
            <div className="col-md-12 col-sm-12">
                <label className="form-label" style={styles.InputText}>Mục đích sử dụng</label>
                <textarea className="form-control" name="purpose" rows="3" cols="20" value={inputs.purpose || ""} onChange={(event)=>onChange(event)}/>
            </div>
            <div className="col-md-12 col-sm-12">
                <label className="form-label" style={styles.InputText}>Ghi chú</label>
                <textarea className="form-control" name="note" rows="3" cols="20" value={inputs.note || ""} onChange={(event)=>onChange(event)}/>
            </div>
            <div className="col-md-12 col-sm-12">
                <label className="form-label" style={styles.InputText}>Chi tiết hàng hóa</label>
                <DataTable value={item} paginator showGridlines rows={10} 
                           globalFilter={globalFilter} header={header}
                           emptyMessage="Không có dữ liệu" style={styles.dataTable}>
                    <Column field="STT" header="STT" sortable style={{ width: '5%' }}></Column>
                    <Column field="ItemCode" header="Mã hàng" sortable style={{ width: '10%' }}></Column>
                    <Column field="ItemName" header="Tên hàng" sortable style={{ width: '60%' }}></Column>
                    <Column field="Unit" header="ĐVT" sortable style={{ width: '5%' }}></Column>
                    <Column field="Quantity" header="Số lượng" sortable style={{ width: '10%' }}></Column>
                    <Column field="Status" header="Thao tác" body={activityBodyTemplate} style={{ width: '10%' }}></Column>
                </DataTable>
            </div>
            <br/>
            <div className="d-grid gap-3 col-md-3 col-sm-3 col-3">
                <button style={styles.InputText} data-bs-toggle="modal" data-bs-target="#addItem" className="btn btn-outline-primary">Thêm</button>
            </div>
        </form>
    )
}

export default memo(Form);
const styles = {
    title:{
        textAlign:"center",
        color:"red",
        padding:"10px",
        fontFamily:"Times New Roman"
    },
    InputText:{
        fontFamily:"Times New Roman"
    },
    dataTable:{
        fontFamily:"Times New Roman"
    },
    margin_top:{
        marginTop:"20px",
        fontFamily:"Times New Roman",
        marginBottom:"20px",
    },
}
