import { Link } from "react-router-dom";
import React, { useState } from 'react';
import { Sidebar } from 'primereact/sidebar';
import { Button } from 'primereact/button';
//import { SplitButton } from 'primereact/splitbutton';
import { useNavigate } from 'react-router-dom';

function Header() {
  const [visible, setVisible] = useState(false);
  const navigate = useNavigate();
  
  const handleSignUp = () => {
    localStorage.removeItem("user");
    navigate("/login");
  }
//   const items = [
//     {
//         label: 'Đăng xuất',
//         icon: 'pi pi-sign-out',
//         command: () => {
         
//         }
//     }
// ];

  return ( 
  <div className="nav">
    <Sidebar className="nav-header" visible={visible} onHide={() => setVisible(false)}>
        <div className="nav-menu">
          <div className="logo">
            <img src="img/logoBitex.png" width="100px" height="100px" alt="logo"/>
          </div>
       <div className="navbar">
        <ul className="menu">
            <li><i className="fa-solid fa-house"></i><Link to="/bill">Home</Link></li>
            <li><i className="fa-solid fa-list-check"></i><Link to="/staff">Check hóa đơn giao khách hàng</Link></li>
            {/* <li>Clothins
                <label>
                    <input type="checkbox"/>
                    <i className="fa-solid fa-angle-down"></i>
                </label>
                <ul>
                    <li>All Clothings</li>
                    <li>Shirts
                        <label>
                            <input type="checkbox"/>
                            <i className="fa-solid fa-angle-down"></i>
                        </label>
                        <ul>
                            <li>T-Shirts</li>
                            <li>Hoodie</li>
                            <li>Sweatershirt</li>
                            <li>1</li>
                            <li>2</li>
                        </ul>
                    </li>
                    <li>Shorts</li>
                    <li>Caps</li>
                    <li>Socks</li>
                </ul>
            </li> */}
            <li><i className="fa-solid fa-list-check"></i><Link to="/bill">Check hóa đơn</Link></li>
            <li> <i className="fa-solid fa-list-ul"></i><Link to="/">Thuế</Link></li>
          
        </ul>
        <ul className="otherOption">
            <li><i className="fa-solid fa-user"></i>{JSON.parse(localStorage.getItem('user'))}</li>
            <li onClick={handleSignUp}><i className="fa-solid fa-right-to-bracket"></i>Đăng xuất</li>
        </ul>
       </div>
        </div>
    </Sidebar>
    <Button style={styles.menu} icon="pi pi-bars" onClick={() => setVisible(true)} outlined />
    {/* <SplitButton label={JSON.parse(localStorage.getItem('user'))} icon="pi pi-user" model={items} outlined /> */}
  </div>
  );
}

export default Header;

const styles = {
  menu:{
    marginLeft: "5px"
  }
}

