import { useState, memo, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { postApi } from '../../api';
import { Toast } from 'primereact/toast';
import { InputText } from "primereact/inputtext";
import { Password } from 'primereact/password';

function Login() {
  const [inputs, setInputs] = useState({});
  const navigate = useNavigate();
  const toast = useRef(null);
  
  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }

  const showError = () => {
    toast.current.show({severity:'error', summary: 'Error', detail:'Đăng nhập thất bại!', life: 3000});
  }
  
  const handleSubmit = (event) => {
    event.preventDefault();
    let formData = new FormData(); 
    formData.append('email', inputs.email);
    formData.append('password', inputs.password);
  
    postApi("auth/login", formData).then((result) =>{
      if(result.access_token === 0){
        showError();
      }else{
        localStorage.setItem('access_token', JSON.stringify(result.access_token));
        navigate("/purchasing");
        navigate(0);
      }
     })
  }


 return ( 
  <div className="login">
     <div className="login-box">
          <div className="logo">
             <img src="img/logoBitex.png" alt="Logo" />
          </div>
          <form onSubmit={handleSubmit}>
          <span className="p-float-label">
                <InputText id="email" type="email" onChange={handleChange} name="email" value={inputs.email || ""}  required />
                <label htmlFor="email">Tên đăng nhập</label>
          </span>
          <span className="p-float-label">
              <Password  id="password" type="password" onChange={handleChange} name="password" value={inputs.password || ""} toggleMask  feedback={false} required />
              <label htmlFor="password">Mật khẩu</label>
          </span>
            <input type="submit" value="Đăng nhập"  />
      </form>
      <Toast ref={toast} />
     </div>
   </div>
   
 );
}

export default memo(Login);
