import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import * as ACTIONS from '../../store/actions/index';
import TableLine from './tableLine';

//import Modal from './modal'
function Bill() {
    
    const [inputs, setInputs] = useState({});
    // const [message, setMessage] = useState()
    // const [infoCustomer, setInfoCustomer] = useState({})

    const dispatch = useDispatch()

    const bill = useSelector((state) => state.bill); /* đơn hàng */
    
    /* set input đưa vào setInputs  */ 
    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    /* set date */
    useEffect(() => {
        let date = new Date()
        var y = date.getFullYear()
        var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
        var d = date.getDate() > 9 ? date.getDate() : "0"+date.getDate()
        setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
    }, [])

    /* lấy đơn hàng */
    useEffect(() => {
        dispatch(ACTIONS.getBill()) 
     },[dispatch])
    
   
    /* thay doi trang thai chi tiet don hang */
    const handeChangeStatusBillLine = (EI, DocumentNo) => {
        EI ?
        dispatch(ACTIONS.setStatusBill(EI, DocumentNo))
        : alert("Thiếu thông tin Hóa đơn")
    }
    
    return ( 
    <div className="row">
        <div className="col-12 col-sm-12">
            <h1 className="title">Danh sách hóa đơn</h1> 
        </div>
        <form className="row g-3">
            <div className="col-md-6">
                <label className="form-label">Từ ngày</label>
                <input type="date" className="form-control" name="formDate" value={inputs.formDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6">
                <label className="form-label">Đến ngày</label>
                <input type="date" className="form-control" name="toDate" value={inputs.toDate || ""} onChange={(event)=>onChange(event)} />
            </div>
        </form>
        
        <table className="table table-striped table-hover">
            <thead>
                <tr>
                    <th scope="col">Hóa đơn</th>
                    <th scope="col">Số hóa đơn</th>
                    <th scope="col">Chứng từ</th>
                    <th scope="col">Mã khách hàng</th>
                    <th scope="col">Tên khách hàng</th>
                    <th scope="col">Địa chỉ</th>
                    <th scope="col">Trạng thái</th>
                </tr>
            </thead>
            <tbody>
                <TableLine bill = { bill ? bill : null } handeChangeStatusBillLine = { handeChangeStatusBillLine } />
            </tbody>
        </table>
        {/* <div className="col-12">
            <div className="col-md-12" style={styles.padding}>
        <button className="btn btn-outline-dark d-flex justify-content-center" 
                data-bs-toggle="modal" data-bs-target="#ThongBao" 
                style={styles.button}
                onClick={(e) => handeChangeFinish(e)}>Hoàn tất đơn soạn
        </button>
        </div> 
        </div> */}
        
        {/* Modal */}
        {/* <div className="modal" id="ThongBao" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="staticBackdropLabel">Thông báo</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                  <span>{message}</span>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              </div>
            </div>
          </div>
        </div> */}
        {/* Modal */}
    </div>
    );
}

export default Bill;

const styles = {
    button : {
      padding:"15px",
      borderRadius:"5px",
      margin: "0 auto",
      display: "flex",
      justifyContent: "center"
    },
    padding:{
      padding:"50px"
    },
    test:{
        padding:"100px",
        background: "#3c197d"
    }
}
