import { getApi, postApi } from "../../api";
import * as Types from "../type/index"

/* đăng nhập */
export const getLogin = (formData) => {
  return (dispatch) => {
    postApi("login", formData).then((result) =>{
      return dispatch({ type: Types.LOGIN, payload: result.data[0].Code })
    })
  }
};

/* get kho theo user */
export const getLocationByUser = () => {
  return (dispatch) => {
    getApi("location/thanhnguyen", formData).then((result) =>{
      return dispatch({ type: Types.GETLOCATIONBYUSER, payload: result.data })
    })
  }
};

/* lấy danh sách thuế */
export const getTax = () => {
  return (dispatch) => {
      getApi("tax").then((result) =>{
        return dispatch({ type: Types.GETTAX, payload: result.data })
      })
  }
};

/* lấy danh hóa đơn */
export const getBill = (formData) => {
  return (dispatch) => {
    postApi("getBill", formData).then((result) =>{
        return dispatch({ type: Types.GETBILLL, payload: result.data })
    })
  }
};

/* update trạng thái hóa đơn */
export const updateStatusBill = (id) => {
  return (dispatch) => {
    return dispatch({ type: Types.UPDATE_STATUS_BILL, payload: id })
  }
};


/*
 * Các Action: Khi ta định nghĩa các action, ta khai báo các tên của hành động trong ứng dụng. 
 * dispatch la 1 ham
 */
