import { memo } from "react";

function TableLine ( props ) {
  const { bill,handeChangeStatusBillLine } = props
  console.log("re-reder Table");
    return (
    bill && bill.length > 0 ?
    bill.map((item,index) => {
      return (
        <tr key={index} className="tr-hover">
           <td>{item.EI}</td>
           <td>{item.Number}</td>
           <td>{item.DocumentNo}</td>
           <td>{item.CustomerCode}</td>
           <td>{item.CustomerName}</td>
           <td>{item.Address}</td>
           <td>{ item.Status==="0" ? <button className='btn btn-outline-info' data-bs-toggle="modal" data-bs-target="#ThongBao" onClick={()=>handeChangeStatusBillLine(item.EI,item.DocumentNo)}>Chưa Lấy</button> 
               : item.Status==="1" ? <p className="text-primary">Đã Lấy</p> : ""
            }</td>
        </tr>
      )
    })
    :
    <tr>
        <td colSpan={7} style={{'textAlign':'center',"color":"red"}}>Không có dữ liệu</td>
    </tr>
  );
}

export default memo(TableLine);
