import { useState, useEffect, useRef } from 'react';
import { getApi } from '../../../api';

const SearchableSelect = ({ onChange }) => {
  const [item, setItem] = useState([]);
  const [selected, setSelected] = useState(item[0]);
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');
  const ref = useRef();

  useEffect(() => {
        const fetchItems = async () => {
          try {
            const result = await getApi("item");
            setItem(result.data);
          } catch (error) {
            console.error("Lỗi khi gọi API Item:", error);
          }
        };
        
        fetchItems();
      }, [])

  const filteredOptions = item.filter(o =>
    o.label.toLowerCase().includes(search.toLowerCase())
  );

  const handleSelect = (option) => {
    setSelected(option);
    setSearch('');
    setIsOpen(false);
    onChange && onChange(option);
  };

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (ref.current && !ref.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="position-relative w-100" ref={ref}>
      <div className="form-select"
           onClick={() => setIsOpen(!isOpen)}
          // style={{ cursor: 'pointer' }}
           >
        {selected}
      </div>

      {isOpen && (
        <div className="dropdown-menu show w-100 p-2 shadow-sm">
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Tìm kiếm..."
            autoFocus
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          {filteredOptions.map(opt => (
            <button
              key={opt.Code}
              className="dropdown-item"
              type="button"
              onClick={() => handleSelect(opt)}
            >
              {opt.Name}
            </button>
          ))}
          {filteredOptions.length === 0 && (
            <div className="dropdown-item text-muted">Không tìm thấy</div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchableSelect;
