import { memo } from 'react';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';
import { InputNumber } from 'primereact/inputnumber';
import { RadioButton } from "primereact/radiobutton";
import { InputTextarea } from 'primereact/inputtextarea';

function Modal( props ) {
    const { message, visible, description, handeInsertListOrderDelivered, handleChangeDescription, handeCloseModal, handeOpenModal, staff, ingredient, number,  handleChangeSelect, handleChangeRadio, handleChangeInputNumber } = props;
 
    const footerContent = (
      <div>
          <Button style={styles.text} label="Thoát" icon="pi pi-times" onClick={ handeCloseModal } className="p-button-text" severity="secondary" rounded text raised/>
          <Button style={styles.text} label="Đồng ý" icon="pi pi-check" onClick={()=>handeInsertListOrderDelivered()} severity="success" autoFocus rounded />
      </div>
    );

    return (
    <div className="modal fade" id="ThongBao" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={styles.text} id="staticBackdropLabel">Chi tiết đơn hàng</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="card flex justify-content-center" style={styles.bodyModal}>
                  <span style={styles.text}>{message.Code}</span>
                  <span style={styles.text}>{message.DocumentNo}</span>
                  <span style={styles.text}>{message.CustomerCode}</span>
                  <span style={styles.text}>{message.CustomerName}</span>
                  <span style={styles.text}>{message.Address}</span>
                  <span style={styles.text}>{message.Description}</span>
                  <span style={styles.text}>{message.Industry}</span>
                  <span style={styles.text}>{message.DocumentDate}</span>
                 <br/>
                 <form>
                  <select className="form-select" style={styles.text} onChange ={ handleChangeSelect }>
                  <option value="">Chọn nhân viên giao hàng</option>
                  { staff && staff.length>0  ? 
                     staff.map((item, index) => {
                      return ( <option key={index} value={item.CODE}>{item.CODE} - {item.NAME}</option>) }) : ""  
                  }
                </select>
                <div className="flex align-items-center" style={styles.radio}>
                    <RadioButton inputId="ingredient1" name="pizza" value="1" onChange={ handleChangeRadio } checked={ingredient === '1'} />
                    <label style={styles.label} htmlFor="ingredient1" className="ml-2">Xe máy</label>
                    <RadioButton inputId="ingredient2" name="pizza" value="2" onChange={ handleChangeRadio } checked={ingredient === '2'} />
                    <label style={styles.label} htmlFor="ingredient2" className="ml-2">Xe ô tô</label>
                </div>
                <label style={styles.text} htmlFor="number-input">Số kiện</label>
                <InputNumber value={number} maxLength="5" onValueChange ={ handleChangeInputNumber } required />
                 <label style={styles.text} htmlFor="description">Ghi chú</label>
                 <InputTextarea style={styles.text} rows={2} cols={30} value ={ description } onChange = { handleChangeDescription }/>
                 </form>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-outline-secondary" data-bs-dismiss="modal" style={styles.text}>Thoát</button>
                <button type="button" className="btn btn-outline-primary"  data-bs-dismiss={ ingredient && number ? "modal" : "" }  onClick={ handeOpenModal } style={styles.text}>Giao đơn hàng</button>
              </div>
            </div>
          </div>
          <Dialog header="Xác nhận" style={styles.text} visible={visible} onHide={ handeCloseModal} footer = {footerContent}>
                <p className="m-0">
                  <span>Bạn có chắc chắn giao đơn hàng này không?</span>
                </p>
            </Dialog>
    </div>
    )
}

export default memo(Modal);

const styles = {
  bodyModal: {
  borderRadius: "10px",
  boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.5)",
  padding: "20px"
  },
  text : {
    fontFamily:"Times New Roman",
    fontWeight: 500
  },
  radio : {
    fontFamily:"Times New Roman",
    fontWeight: 500,
    margin:"15px 0px 30px"
  },
  label : {
    margin:"0px 10px 0px"
  }
}
