import { memo, useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Panel } from 'primereact/panel';
import { Toast } from 'primereact/toast';
import * as ACTIONS from '../../../store/actions/index';
import Form from './form';

function Detail() {
  const status = useSelector((state) => state.status);
  const departments = useSelector((state) => state.department); 
  const dispatch = useDispatch();

  const [inputs, setInputs] = useState({});
  const [selectedDepartment, setSelectedDepartment] = useState([]);
  const [selectedStatus, setselectedStatus] = useState([]);
  const toast = useRef(null);
  
  useEffect(() => {
    let date = new Date()
    var y = date.getFullYear()
    var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
    var d = date.getDate() > 9 ? date.getDate() : "0" + date.getDate()
    setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
  }, [])
  
  /* Lấy dữ liệu từ api */
  useEffect(() => {
    dispatch(ACTIONS.getDepartments()) 
    dispatch(ACTIONS.getStatus()) 
  }, [dispatch])
    
  const onChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }
  
  const handleChangeStatus = (event)  => {
    setselectedStatus(event.target.value)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const department = selectedDepartment.join(',');
    if(!department){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn phòng ban", life: 3000});
    }
    console.log("department: " + department);
    console.log("selectedStatus: " + selectedStatus);
    console.table(inputs.formDate, inputs.toDate);
    
  }

  
  return ( 
    <div className="row">
      <Panel style={styles.header} header="Phê duyệt đơn đặt mua hàng">
        <Form departments = {departments} status = {status} inputs = {inputs} onChange = {onChange}
              handleChangeStatus = {handleChangeStatus}
              selectedDepartment = {selectedDepartment}
              setSelectedDepartment = {setSelectedDepartment}
              handleSubmit = {handleSubmit}/>
      </Panel>
    </div>
  );
}

export default memo(Detail);
const styles = {
   header:{  fontFamily:"Times New Roman" }
}
