import { useEffect, useState } from 'react'
//import { useSelector, useDispatch } from 'react-redux'
//import * as ACTIONS from '../../store/actions/index'
import Table from './table';
import { getApi } from '../../api';

function Home() {
    
    // const order= useSelector((state) => state.order);
    // const dispatch = useDispatch()

    // useEffect(() => {
    //     dispatch(ACTIONS.getWSOrderDetail())
    //  },[dispatch])

    const [order, setOrder] = useState();
    const [location, setLocation] = useState();
    const [inputs, setInputs] = useState({});
     /* GET input đưa vào setInputs  */ 
    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    //const [dateHienTai, setDateHienTai] = useState()
    let date = new Date(), y = date.getFullYear(), m = date.getMonth() + 1;
    var firstDay = new Date(y, m, 1).toString("dd/MM/yyyy");
    //setDateHienTai(date)
    console.log(firstDay)
    console.log(inputs)
    
    useEffect(() => {
        getApi("getOrderDetail").then((res)=>{
            setOrder(res.data);
        })

        getApi("getLocation").then((res)=>{
            setLocation(res.data);
        })

      }, [])

      let formData = new FormData(); 
      formData.append('location', inputs.location);
      
      let locations = inputs.location;

      locations ?  getApi('getOrderHeader/'.locations).then((res)=>{
        console.log(res.data);
    }) : null;
    

    return ( 
    <div className="row">
        <div className="cards ">
            <div className="card">
                <div className="input-groups">
                    <label className="col-form-label">Từ ngày</label>
                    <input type="date" className="form-control" name="formDate" onChange={(event)=>onChange(event)} />
                </div>
            </div>
            <div className="card">
                <div className="input-groups">
                    <label className="col-form-label">Đến ngày</label>
                    <input type="date" className="form-control" name="toDate" onChange={(event)=>onChange(event)}/>
                </div>
            </div>
            <div className="card">
                <div className="input-groups">
                    <label className="col-form-label">Kho</label>
                    <select className="form-select" id="location" name="location" onChange={(event)=>onChange(event)}>
                        { location && 
                          location.map((item,index) => {
                            return (<option value={item.NO} key={index}>{item.Name}</option>)
                          })
                        }
                    </select>
                </div>
            </div>
        </div>
        
        <table className="table table-striped table-hover">
            <thead>
                <tr>
                    <th scope="col">Item Code</th>
                    <th scope="col">Item Name</th>
                    <th scope="col">Variant</th>
                    <th scope="col">Location</th>
                    <th scope="col">Lot</th>
                    <th scope="col">SL</th>
                    <th scope="col">DVT</th>
                    <th scope="col">MaDH</th>
                    <th scope="col">Status</th>
                    <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>
                <Table order = { order ? order : null }/>
            </tbody>
        </table>
    </div>
    );
}

export default Home;
