import * as types from '../type/index';
import { search } from '../../hooks';

const initialState =[]

const reducerOrder = (state = initialState, action) => {

  const { type, payload } = action;  

  switch (type) {
    case types.GETORDERHEADER:  /* Lấy đơn hàng Header */
      payload ? state = payload :  state = []
      return [...state] 
    case types.SETSTATUSORDERHEADER:  /* set status đơn hàng Header */
      return [...state] 
    case types.FILERSTATUSORDERHEADER:
      state = search(state,payload)
      console.log(state);
      return [...state]
    case types.FILERSEARCHORDERHEADER:
      let t = state
      let data = search(t, payload)
      return [...data]
    default:
      return state

  }
}

export default reducerOrder;
