import { memo } from 'react';
import { Dialog } from 'primereact/dialog';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
function ShowDialog( props ) {
 const { selectedBill, dialogVisible, handeCloseBillAll,  handeOpenBillAll, dialogFooterTemplate, showImage, setShowImage, setImage} = props;
 
 const uploadImage = (event) => {
  if(event.target.files && event.target.files.length > 0){
     setImage(event.target.files[0])
     setShowImage(URL.createObjectURL(event.target.files[0]))
   }
 }
    return (
     <>
     <Button style={styles.margin_top} label="Upload ảnh cho nhiều đơn hàng" icon="pi pi-external-link" onClick={ () => handeOpenBillAll()} />
     <Dialog style={styles.text} header="Đơn hàng đã chọn" visible={dialogVisible} maximizable
                   modal contentStyle={{ height: '300px' }} onHide={() => handeCloseBillAll()} footer={dialogFooterTemplate}>
        <DataTable value={selectedBill} editMode="row" scrollable scrollHeight="flex" style={styles.dataTable} >
          <Column field="Code" header="Số hóa đơn" style={{ width: '20%' }}></Column>
          <Column field="Name" header="Tên khách hàng" style={{ width: '80%' }}></Column>
        </DataTable>
        <div className="col-md-12">
            <label className="form-label" style={styles.text}>Image</label>
            <input type="file" className="form-control" style={styles.input} size = {[400, 200]} placeholder='Upload an image' onChange={(event)=>uploadImage(event)}/>
        </div>
        <div className="col-md-12">
           {showImage &&  <img src={showImage} style={styles.image} height="180px" width="200px" alt="img"/> }
        </div>
     </Dialog>
     </>
    )
}

export default memo(ShowDialog);

const styles = {
 dataTable:{ fontFamily:"Times New Roman" },
 image: { borderRadius:"10px", marginTop: "10px"},
 input: { fontFamily:"Times New Roman"},
 text : { fontFamily:"Times New Roman", fontWeight: 500 },
}
