import { useEffect, useState, memo, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import * as ACTIONS from '../../store/actions/index';
import Modal from './modal';
import { postApi } from '../../api';
import { Toast } from 'primereact/toast';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Panel } from 'primereact/panel';

function Bill() {

    const bill = useSelector((state) => state.bill); 
    const dispatch = useDispatch();
    const [inputs, setInputs] = useState({});
    const [dataEIDocumentNo, setdataEIDocumentNo] = useState()
    const [message, setMessage] = useState([])
    const [globalFilter, setGlobalFilter] = useState(null);
    const toast = useRef(null);
    
    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    useEffect(() => {
        let date = new Date()
        var y = date.getFullYear()
        var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
        var d = date.getDate() > 9 ? date.getDate() : "0"+date.getDate()
        setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
    }, [])
   
    const handleSubmit = (event) => {
        event.preventDefault();
        let formData = new FormData(); 
        formData.append('formDate', inputs.formDate);
        formData.append('toDate', inputs.toDate);
        dispatch(ACTIONS.getBill(formData));
    }

    const handeChangeStatusBillLine = (EI, DocumentNo) => {
        let formData = new FormData()
        formData.append('EI', EI);
        formData.append('documentNo', DocumentNo);
        setdataEIDocumentNo({EI, DocumentNo});
       
        postApi("getBillDetail", formData).then((result) =>{
            setMessage(result.data[0]);
        })
    }
    
    const handeInsertListOrderDelivered = () => {
        let formData = new FormData(); 
        let userCode = JSON.parse(localStorage.getItem('user'));
        formData.append('EI', dataEIDocumentNo.EI);
        formData.append('documentNo', dataEIDocumentNo.DocumentNo);
        formData.append('userCode', userCode);

        postApi("insertListOrderDelivered", formData).then((result) =>{
            let message = result.data[0].Mes;
            switch (message) {
                case '0':
                    return toast.current.show({severity:'warn', summary: 'Warning', detail:`Hóa đơn ${dataEIDocumentNo.EI} đã giao rồi`, life: 3000});
                case '1':
                    return showSuccess(dataEIDocumentNo.EI);
                case '2':
                    return toast.current.show({severity:'warn', summary: 'Warning', detail:'User này không được phân quyền cho công việc này', life: 3000});
                default:
                    return "";
            }
        })
    }
    
    const showSuccess = (EI) => {
        toast.current.show({severity:'success', summary: 'Success', detail:`Hóa đơn ${dataEIDocumentNo.EI} giao thành công`, life: 3000});
        dispatch(ACTIONS.updateStatusBill(EI));
    }

    
    const renderHeader = () => {
        return (
        <div className="flex justify-content-between">
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
        );
    };

    const header = renderHeader();
    
    const getSeverity = (status, EI, DocumentNo) => {
        switch (status) {
            case '0':
                return <Button type="button" data-bs-toggle="modal" data-bs-target="#ThongBao" onClick={()=>handeChangeStatusBillLine(EI, DocumentNo)} icon="pi pi-cog" rounded></Button>;
            case '1':
                return <i className="pi pi-check" style={{ color: 'slateblue' }}></i>; 
            default:
                return "";
        }
    };

    const activityBodyTemplate = (rowData) => {
        return getSeverity(rowData.Status, rowData.EI, rowData.DocumentNo)
    };

    return ( 
    <div className="row">
        <Panel header="Danh sách hóa đơn">
        <form className="row g-3" onSubmit = { handleSubmit }>
            <div className="col-md-3 col-sm-3">
                <label className="form-label" style={styles.InputText}>Từ ngày</label>
                <input type="date" style={styles.InputText} className="form-control" name="formDate" value={inputs.formDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-3 col-sm-3">
                <label className="form-label" style={styles.InputText}>Đến ngày</label>
                <input type="date" style={styles.InputText} className="form-control" name="toDate" value={inputs.toDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6 col-sm-6">
            </div>
            <br/>
            <div className="d-grid gap-2 col-md-2 col-sm-2 col-6">
                <button type="submit" style={styles.InputText} className="btn btn-outline-primary">Lọc</button>
            </div>
        </form>
        </Panel>
        <DataTable value={bill} paginator showGridlines rows={200} 
                globalFilter={globalFilter} header={header}
                emptyMessage="Không có dữ liệu" style={styles.dataTable}>
            <Column field="EI" header="Hóa đơn" sortable style={{ width: '10%' }}></Column>
            <Column field="Number" header="Số hóa đơn" sortable style={{ width: '10%' }}></Column>
            <Column field="DocumentNo" header="Chứng từ" sortable style={{ width: '20%' }}></Column>
            <Column field="CustomerCode" header="Mã khách hàng" sortable style={{ width: '20%' }}></Column>
            <Column field="CustomerName" header="Tên khách hàng" sortable style={{ width: '20%' }}></Column>
            <Column field="DocumentDate" header="Ngày" sortable style={{ width: '10%' }}></Column>
            <Column field="Status" header="Trạng thái" sortable body={activityBodyTemplate} style={{ width: '10%' }}></Column>
        </DataTable>
       
        <Modal message = {message ? message : null } handeInsertListOrderDelivered = { handeInsertListOrderDelivered } />
        <Toast ref={toast} />
    </div>
    );
}

export default memo(Bill);

const styles = {
    title:{
        background: "#062544"
    },
    margin_left:{
        marginLeft:"15px",
        marginTop:"2px",
        fontFamily:"Times New Roman"
    },
    dataTable:{
        fontFamily:"Times New Roman"
    },
    InputText:{
        fontFamily:"Times New Roman"
    }
}
