import { memo, useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Panel } from 'primereact/panel';
import { Toast } from 'primereact/toast';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import * as ACTIONS from '../../../store/actions/index';
import { postApiv1 } from '../../../api';
import Form from './form';
import Modal from './modal';

function Detail() {
  const orderapproval = useSelector((state) => state.orderapproval);
  const status = useSelector((state) => state.status);
  const departments = useSelector((state) => state.department); 
  const dispatch = useDispatch();
  
  const [detailOrder, setDetailOrder] = useState([]);
  const [inputs, setInputs] = useState({});
  const [selectedDepartment, setSelectedDepartment] = useState([]);
  const [selectedStatus, setselectedStatus] = useState([]);
  const [globalFilter, setGlobalFilter] = useState(null);
  const toast = useRef(null);

  useEffect(() => {
    let date = new Date()
    var y = date.getFullYear()
    var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
    var d = date.getDate() > 9 ? date.getDate() : "0" + date.getDate()
    setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
  }, [])
  
  
  /* Lấy dữ liệu từ api */
  useEffect(() => {
    dispatch(ACTIONS.getDepartments()) 
    dispatch(ACTIONS.getStatus()) 
    dispatch(ACTIONS.approval()) 
  }, [dispatch])
    
  const onChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }
  
  const handleChangeStatus = (event)  => {
    setselectedStatus(event.target.value)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const department = selectedDepartment.join(',');
    
    if(!department){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn phòng ban", life: 3000});
    }
    console.log("department: " + department);
    console.log("selectedStatus: " + selectedStatus ? selectedStatus : 1);
    console.table(inputs.formDate, inputs.toDate);
    
  }
  
  /* lấy chi tiết đơn đặt hàng */
  const handeChangeDetail = async(document) => {
    try {
      let formData = new FormData(); 
      formData.append('document', document);
      const response = await postApiv1('orderpurchasing/document', formData); 
      setDetailOrder(response.data);
    } catch (error) {
      return toast.current.show({severity:"error", summary: "Error", detail:"Lỗi khi tải dữ liệu", life: 3000});
    }
  }
  
  /* cập nhập trạng thái */
  const handeModalUpdateStatus = async () => {
    if(detailOrder.length > 0){
      let formData = new FormData(); 
      formData.append('document', detailOrder[0].DocumentNo);
      formData.append('status', '3');
      await postApiv1('orderpurchasing/updatestatus', formData); 

      return toast.current.show({severity:"success", summary: "Success", detail:"Duyệt đơn hàng thành công", life: 3000});
    }else{
      return toast.current.show({severity:"error", summary: "Error", detail:"Lỗi duyệt đơn", life: 3000});
    }
  }

  const getLineInformation = (document) => {
    return (<button className="btn btn-outline-primary" style={styles.InputText} data-bs-toggle="modal" data-bs-target="#detail" onClick={()=>handeChangeDetail(document)}>Xem chi tiết</button>)   
  };

  const activityBodyTemplate = (rowData) => {
    return getLineInformation(rowData.DocumentNo)
  };

  const renderHeader = () => {
    return (<div className="flex justify-content-between">
              <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
              </span>
            </div>);
  };
  const header = renderHeader();

  return ( 
    <div className="row">
      <Panel style={styles.header} header="Phê duyệt đơn đặt mua hàng">
        <Form departments = {departments} status = {status} inputs = {inputs} onChange = {onChange}
              handleChangeStatus = {handleChangeStatus}
              selectedDepartment = {selectedDepartment}
              setSelectedDepartment = {setSelectedDepartment}
              handleSubmit = {handleSubmit}/>
        <br/>
        <DataTable value={orderapproval} paginator showGridlines rows={10} 
                   globalFilter={globalFilter} header={header}
                   emptyMessage="Không có dữ liệu" style={styles.dataTable}>
          <Column field="DocumentNo" header="Mã" sortable style={{ width: '10%' }}></Column>
          <Column field="Department" header="Phòng ban" sortable style={{ width: '30%' }}></Column>
          <Column field="PostingDate" header="Ngày cần hàng" sortable style={{ width: '15%' }}></Column>
          <Column field="CreatedDate" header="Ngày tạo" sortable style={{ width: '15%' }}></Column>
          <Column field="StatusName" header="Trạng thái" sortable style={{ width: '15%' }} 
            body={(rowData) => {
              const color = rowData.StatusName === 'Chờ duyệt' ? 'darkorange' : 'darkgreen';
                return <span style={{ color }}>{rowData.StatusName}</span>;
            }}>
          </Column>
          <Column field="Status" header="Thao tác" body={activityBodyTemplate} style={{ width: '15%' }}></Column>
        </DataTable>
        <Modal detailOrder = {detailOrder} handeModalUpdateStatus = {handeModalUpdateStatus}/>
      </Panel>
      <Toast ref = {toast}/>
    </div>
  );
}

export default memo(Detail);
const styles = {
  header: {fontFamily:"Times New Roman"},
  InputText: {fontFamily:"Times New Roman"},
  dataTable: {fontFamily:"Times New Roman"},
  title:{
    textAlign:"center",
    color:"red",
    padding:"10px",
    fontFamily:"Times New Roman"
  },
  margin_top:{
    marginTop:"20px",
    fontFamily:"Times New Roman",
    marginBottom:"20px",
  },
}
