import { memo } from 'react';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';

function Modal( props ) {
    const { message, visible, handeInsertListOrderDelivered, handeCloseModal, handeOpenModal, staff, handleChangeSelect } = props;
    
    const footerContent = (
      <div>
          <Button label="Thoát" icon="pi pi-times" onClick={ handeCloseModal } className="p-button-text" severity="secondary" rounded text raised/>
          <Button label="Đồng ý" icon="pi pi-check" onClick={()=>handeInsertListOrderDelivered()} severity="success" autoFocus rounded />
      </div>
    );
    
    return (
    <div className="modal fade" id="ThongBao" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="staticBackdropLabel">Chi tiết đơn hàng</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="card flex justify-content-center" style={styles.bodyModal}>
                  <span style={styles.text}>{message.EI}</span>
                  <span style={styles.text}>{message.Number}</span>
                  <span style={styles.text}>{message.DocumentNo}</span>
                  <span style={styles.text}>{message.CustomerCode}</span>
                  <span style={styles.text}>{message.CustomerName}</span>
                  <span style={styles.text}>{message.Address}</span>
                  <span style={styles.text}>{message.Description}</span>
                  <span style={styles.text}>{message.DocumentDate}</span>
                 <br/>
                  <select className="form-select" style={styles.formSelect} onChange ={ handleChangeSelect }>
                  <option value="">Chọn nhân viên giao hàng</option>
                  { staff && staff.length>0  ? 
                     staff.map((item, index) => {
                      return ( <option key={index} value={item.CODE}>{item.CODE} - {item.NAME}</option>) }) : ""  
                  }
                </select>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-outline-secondary" data-bs-dismiss="modal" >Thoát</button>
                <button type="button" className="btn btn-outline-primary" data-bs-dismiss="modal"  onClick={ handeOpenModal } >Giao đơn hàng</button>
              </div>
            </div>
          </div>
          <Dialog header="Xác nhận" visible={visible} onHide={ handeCloseModal} footer = {footerContent}>
                <p className="m-0">
                  <span style={styles.text}>Bạn có chắc chắn giao đơn hàng này không?</span>
                </p>
            </Dialog>
    </div>
    )
}

export default memo(Modal);

const styles = {
  bodyModal: {
  borderRadius: "10px",
  boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.5)",
  padding: "20px"
  },
  text : {
    fontFamily:"Times New Roman",
    fontWeight: 500
  }
}
