import { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Sidebar } from 'primereact/sidebar';
import { Button } from 'primereact/button';
import { useNavigate } from 'react-router-dom';
import { postApiv1 } from '../../../api';
import * as ACTIONS from '../../../store/actions/index';

function Header() {
  const user = useSelector((state) => state.user); 
  const [visible, setVisible] = useState(false);
  const navigate = useNavigate();

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(ACTIONS.getUser())  
  },[dispatch])
 
  const handleSignUp = () => {
    postApiv1("auth/logout").then((result) =>{
      localStorage.removeItem("access_token");
      navigate("/");
    })
  }
  if(!user){
      console.log(user[0].name);
  }
  
  return ( 
    <div className="nav">
      <Sidebar className="nav-header" visible={visible} onHide={() => setVisible(false)} >
        <div className="nav-menu">
          <div className="logo">
            <img src="img/logoBitex.png" width="100px" height="100px" alt="logo"/>
          </div>
          <div className="navbar">
            <ul className="otherOption">
              <li><i className="fa-solid fa-user"></i>{ user[0] ? user[0].name : "" }</li>
              <li onClick={handleSignUp}><i className="fa-solid fa-right-to-bracket"></i>Đăng xuất</li>
            </ul>
          </div>
        </div>
      </Sidebar>
      <Button style={styles.menu} icon="pi pi-bars" onClick={() => setVisible(true)} outlined />
    </div>
  );
}

export default Header;
const styles = {
  menu:{ marginLeft: "5px" }
}
