import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Sidebar } from 'primereact/sidebar';
import { Button } from 'primereact/button';
import { useNavigate } from 'react-router-dom';
import { getApi } from '../../../api';
import * as ACTIONS from '../../../store/actions/index';

function Header() {
  const user = useSelector((state) => state.user); 
  const menu = useSelector((state) => state.menu); 
  const [visible, setVisible] = useState(false);
  const navigate = useNavigate();
  
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(ACTIONS.getUser())  
    dispatch(ACTIONS.getMenu()) 
  },[dispatch])
  
  var level1 = []
  var level2 = []
  
  const getMenu = () => {
    // lấy menu level1
    menu.forEach(item => {
      if(item.Parent === "0"){
        level1.push({Id: item.ID, Name: item.MenuName, Icon: item.Icon, TagCode: item.TagCode})
      }
     });

     // lấy menu level2
     menu.forEach(item => {
      level1.forEach(item1 => {
        if(item.Parent === item1.Id){
          level2.push({Id: item.ID, Name: item.MenuName, Icon: item.Icon, Parent: item.Parent, TagCode: item.TagCode, TagName: item.TagName})
        }
      });
     });
  }

  getMenu();
  
  const getLevel = (FrmCode) => {
    if(FrmCode === null){
      return (
        <label>
          <input type="checkbox"/>
          <i className="fa-solid fa-angle-down"></i>
        </label>
        ) 
    }
  }

  const handleSignUp = () => {
    getApi("logout").then((result) =>{
      console.log(result.message);
    })
    localStorage.removeItem("access_token");
    navigate("/login");
  }
  
  return ( 
    <div className="nav">
    <Sidebar className="nav-header" visible={visible} onHide={() => setVisible(false)} >
        <div className="nav-menu">
          <div className="logo">
            <img src="img/logoBitex.png" width="100px" height="100px" alt="logo"/>
          </div>
       <div className="navbar">
        <ul className="menu">
          { /* menu */ } 
            { level1.map((item, index) => {
                return (
                  <li key={index}> <i className={ item.Icon }></i>
                   <Link to={ item.TagCode }>
                      { item.Name }
                   </Link> 
                     { getLevel(item.TagCode) }
                      { level2.map((item1, index1) => {
                        if(item1.Parent === item.Id){
                          return (
                            <ul key={index1}>
                                  <li><Link to={ item1.TagCode }>{ item1.Name }</Link></li>
                             </ul>
                          )
                        }else { return null }
                       }) 
                       }
                  </li>
                )
            })}
            { /* menu */ }
            <li> <i className="fa-solid fa-list-ul"></i><Link to="/">Thuế</Link></li>
        </ul>
        <ul className="otherOption">
            <li><i className="fa-solid fa-user"></i>{ user ? user : "" }</li>
            <li onClick={handleSignUp}><i className="fa-solid fa-right-to-bracket"></i>Đăng xuất</li>
        </ul>
       </div>
        </div>
    </Sidebar>
    <Button style={styles.menu} icon="pi pi-bars" onClick={() => setVisible(true)} outlined />
  </div>
  );
}

export default Header;

const styles = {
  menu:{ marginLeft: "5px" }
}
