import * as types from '../type/index';

const initialState =[]
const reducerItem = (state = initialState, action) => {
  const { type, payload } = action; 
  switch (type) {
    case types.GET_ITEM:
      if (!payload || payload.length === 0) return state;
      const newItems = payload.filter(
        newItem => !state.some(existing => existing.ItemCode === newItem.ItemCode)
      );
      return [...state, ...newItems];
    case types.UPDATE_ITEM:
      return state.map(item => item.ID === payload.ID
          ? { ...item,
              ItemName: payload.ItemName,
              Unit: payload.Unit,
              Status: payload.Status,
            } : item);
    default:
      return state
  }
}

export default reducerItem;
