import { useEffect, useState, memo } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import * as ACTIONS from '../../store/actions/index';
import TableLine from './tableLine';
import Modal from './modal';
import { postApi } from '../../api';

function Bill() {
    const bill = useSelector((state) => state.bill); 
    const dispatch = useDispatch();
    const [inputs, setInputs] = useState({});
    const [message, setMessage] = useState([])

    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    useEffect(() => {
        let date = new Date()
        var y = date.getFullYear()
        var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
        var d = date.getDate() > 9 ? date.getDate() : "0"+date.getDate()
        setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
    }, [])
   
    const handleSubmit = (event) => {
        event.preventDefault();
        let formData = new FormData(); 
        formData.append('formDate', inputs.formDate);
        formData.append('toDate', inputs.toDate);
        dispatch(ACTIONS.getBill(formData));
    }

    const handeChangeStatusBillLine = (EI, DocumentNo) => {
        let formData = new FormData()
        formData.append('EI', EI);
        formData.append('documentNo', DocumentNo);
        
        postApi("getBillDetail", formData).then((result) =>{
            console.log(result.data[0]);
            setMessage(result.data[0]);
        })
    }
    
    const handeInsertListOrderDelivered = (EI, DocumentNo) => {
        let formData = new FormData(); 
        formData.append('EI', EI);
        formData.append('documentNo', DocumentNo);
        formData.append('userCode', "THANHNGUYEN");
        dispatch(ACTIONS.insertListOrderDelivered(formData));
    }

    return ( 
    <div className="row">
        <div className="col-12 col-sm-12">
            <h1 className="title">Danh sách hóa đơn</h1> 
        </div>
        <form className="row g-3" onSubmit={handleSubmit}>
            <div className="col-md-6">
                <label className="form-label">Từ ngày</label>
                <input type="date" className="form-control" name="formDate" value={inputs.formDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6">
                <label className="form-label">Đến ngày</label>
                <input type="date" className="form-control" name="toDate" value={inputs.toDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-auto">
                <button type="submit" className="btn btn-primary mb-3">Lọc</button>
            </div>
        </form>
        <table className="table table-striped">
            <thead>
                <tr>
                    <th scope="col">Hóa đơn</th>
                    <th scope="col">Số hóa đơn</th>
                    <th scope="col">Chứng từ</th>
                    <th scope="col">Mã khách hàng</th>
                    <th scope="col">Tên khách hàng</th>
                    <th scope="col">Ngày</th>
                    <th scope="col">Trạng thái</th>
                </tr>
            </thead>
            <tbody>
                <TableLine bill = { bill ? bill : null } handeChangeStatusBillLine = { handeChangeStatusBillLine } />
            </tbody>
        </table>
        
        <Modal message = {message ? message : null } handeInsertListOrderDelivered = { handeInsertListOrderDelivered } />
    </div>
    );
}

export default memo(Bill);

