//import { useDispatch } from 'react-redux'
//import * as ACTIONS from '../../store/actions/index'
import { memo, useState, useEffect } from "react";
import ModalItem from "./ModalItem";
import { search } from '../../hooks';

function Modal( props ) {
    //const dispatch = useDispatch()
    const [query, setQuery] = useState('');
    const [data, setData] = useState();
    const { orderHeader, handeOrderLine } = props
    const onChangeStatus = (event) => {
       var {value} = event.target
       console.log(value);
      // dispatch(ACTIONS.filerStatusOrderHeader(value))
    }

    const onSearch = (event) => {
      setQuery(event.target.value)
    }
    
    useEffect(() => {
      setData(search(orderHeader, query))
    },[query,orderHeader,data]);
    

    return (
      <>
        {/* Modal */}
        <div className="modal" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog modal-fullscreen">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="staticBackdropLabel">Danh sách Đơn hàng</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="row g-3">
                  <div className="col">
                    <select className="form-control" name="status" id="status" onChange={(event)=>onChangeStatus(event)}>
                      <option value="3">Tất cả</option>
                      <option value="0">Chưa soạn</option>
                      <option value="1">Đang soạn</option>
                      <option value="2">Hoàn tất</option>
                    </select>
                  </div>
                  <div className="col">
                    <input type="text" className="form-control" onChange={(event) => onSearch(event)} placeholder="Search......"/>
                  </div>
              </div>
              <table className="table table-striped table-hover">
                <thead>
                    <tr> 
                        <th scope="col">STT</th>
                        <th scope="col">Mã kho</th>
                        <th scope="col">Mã đơn hàng</th>
                        <th scope="col">Khách hàng</th>
                        <th scope="col">Trạng thái</th>
                    </tr>
                </thead>
                <tbody>
                    <ModalItem orderHeader = { data ? data : orderHeader} handeOrderLine = { handeOrderLine }/>
                </tbody>
              </table>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              </div>
            </div>
          </div>
        </div>
        {/* Modal */}
      </>
    )
}

export default memo(Modal);
