import { memo } from 'react';

function Modal( props ) {
    const { inputs, onChange, handeModalUpdateItem } = props;
  
    return (
    <div className="modal fade" id="editItem" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={styles.text} id="staticBackdropLabel">Thêm hàng hóa</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="card flex justify-content-center" style={styles.bodyModal}>
                  <div className="col-md-12 col-sm-12">
                    <label className="form-label" style={styles.font}>Tên sản phẩm</label>
                    <input type="text" style={styles.font} className="form-control" name="name" value={inputs.name || ""} onChange={(event)=>onChange(event)} />
                  </div> 
                  <div className="col-md-12 col-sm-12">
                    <label className="form-label" style={styles.font}>ĐVT</label>
                    <input type="text" style={styles.font} className="form-control" name="unit" value={inputs.unit || ""} onChange={(event)=>onChange(event)} />
                  </div>
           
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-outline-secondary" data-bs-dismiss="modal" style={styles.text}>Thoát</button>
                <button type="button" className="btn btn-outline-success"  data-bs-dismiss={ inputs.name && inputs.unit ? "modal" : "" }  onClick = {handeModalUpdateItem} style = {styles.text}>Lưu</button>
              </div>
            </div>
          </div>
    </div>
    )
}

export default memo(Modal);

const styles = {
  bodyModal: {
    borderRadius: "10px",
    boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.5)",
    padding: "20px"
  },
  text : {
    fontFamily:"Times New Roman",
    fontWeight: 500
  },
  radfontio : {
    fontFamily:"Times New Roman"
  },
  label : {
    margin:"0px 10px 0px"
  }
}
